create table customer ( id bigserial primary key, customer_key varchar(255), client_id varchar(255), subscriber_id varchar(500), reference varchar(255), status smallint, last_scan TIMESTAMP WITHOUT TIME ZONE NOT NULL, wait_at_lease TIMESTAMP WITHOUT TIME ZONE NOT NULL, created_by varchar(100), created_date timestamp, last_modified_by varchar(100), last_modified_date timestamp
);
create index customer_wait_at_lease on customer(wait_at_lease) where status = 1;

create table customer_data ( id bigserial primary key, customer_id bigint, type smallint, value varchar(500), created_by varchar(100), created_date timestamp, last_modified_by varchar(100), last_modified_date timestamp
);
create index customer_data_customer_id on customer_data(customer_id);

CREATE INDEX idx_customer_status_created
    ON customer (sync_status, created_date);

CREATE INDEX idx_customer_status_lastscan
    ON customer (sync_status, last_scan);

alter table customer drop column last_scan ;
alter table customer drop column wait_at_lease ;
alter table customer add column last_scan TIMESTAMP WITHOUT TIME ZONE;
alter table customer add column wait_at_lease TIMESTAMP WITHOUT TIME ZONE;

